function render(template, data) &#123;
  &#x2F;&#x2F; 模版字符串正则
  const resg &#x3D; &#x2F;\&#123;\&#123;(\w+)\&#125;\&#125;&#x2F;;
  &#x2F;&#x2F; 判断模版里是否有模版字符串
  if (reg.test(template)) &#123;
    &#x2F;&#x2F; 查找当前模版里第一个模版字符串的字符
    const name &#x3D; reg.exec(template)[1];
    &#x2F;&#x2F; 将第一个模版字符串渲染
